    width: 30px;
    z-index: 1000;
    @media screen and (max-width: 768px) {
      display: inline-block;
    }

    &-line {
      background: $ternary;
      height: 3px;
      position: absolute;
      left: 0;
      transition: all 0.2s ease-out;
      width: 100%;

      .hamburger:hover & {
        background: #777;
      }

      &-top {
        top: 3px;
      }

      .menu-active &-top {
        top: 50%;
        transform: rotate(45deg) translatey(-50%);
      }

      &-middle {
