      @media screen and (max-width: 768px) {
        font-size: 20px;
        margin: 8px;
      }
    }
  }

  .sub-nav {
    border: 1px solid #ccc;
    display: none;
    position: absolute;
    background-color: $primary;
    padding: 5px 5px;
    list-style: none;
    width: 230px;
    @media screen and (max-width: 768px) {
      position: relative;
      width: 100%;
      display: none;
      background-color: rgba(0, 0, 0, 0.2);
      box-sizing: border-box;
    }
  }

  .nav__link {
    &:hover + .sub-nav {
      display: block;
    }
  }

  .sub-nav {
    &:hover {
      display: block;
    }
  }
